import {
    Alert, Text, TextInput, TouchableOpacity,
    View, Keyboard, ScrollView, Image
} from 'react-native';
import { useState, useEffect } from 'react';
import styles from './styles';
import { Ionicons, Entypo } from '@expo/vector-icons';
import api from '../../service/api';

export default function ListaPessoas({navigation}) {
    console.log('aqui');

    const [lista, setLista] = useState([]);
    const [recarregaTela, setRecarregaTela] = useState(true);


    async function processamentoUseEffect() {
        if (!criarTabela) {
            console.log("Verificando necessidade de criar tabelas...");
            setCriarTabela(true);
            await createTable();
        }
        if (recarregaTela) {
            console.log("Recarregando dados...");
            await carregaDados();
        }
    }



    useEffect(
        () => {
            console.log('executando useffect');
            processamentoUseEffect(); //necessário método pois aqui não pode utilizar await...
        }, []);




    async function carregaDados() {
        try {

            let resposta = (await api.get('/pessoa/getAll'));
            console.log(resposta.data);
            setLista(resposta.data);            

        } catch (e) {
            Alert.alert(e.toString());
        }
    }


    return (
        <View>
            <Text>teste</Text>
            <TouchableOpacity onPress={()=> recarregaTela() }>botao</TouchableOpacity>
        </View >        
    );
}
